//program to find area of rectangle using hard coded inputs
/*
Here is the program to find area of a rectangle by providing predefined units.
where, area of rectangle is length*breadth.
*/
#include<stdio.h>
int main()
{
    int length=10;
    int breadth=20;
    int Area;
    Area=length*breadth;
    printf("length is %d\n",length);
    printf("breadth is %d\n",breadth);
    printf("Area of rectangle is %d",Area);
    return 0;
}